hudson.tasks.Shell.command:
# 1) Remove any dangling items
if [ -n "$(docker images --filter dangling=true -q)" ] ; then 
   docker rmi -f $(docker images --filter dangling=true -q); 
   echo "Removed dangling image(s)";
else
   echo "No dangling images to remove";
fi
if [ -n "$(docker volume ls -qf dangling=true)" ] ; then 
   docker volume rm $(docker volume ls -qf dangling=true); 
   echo "Removed dangling volume(s)";
else 
   echo "No dangling volumes to remove";
fi

# 2) Define variables.
# NOTE: JENKINS_URL is the full URL of Jenkins, like http://server:port/jenkins/ 
#       Only available if Jenkins URL is set in system configuration
ECR_REPO_URL="${ECR_REGISTRY_URL}/${REGISTRY_REPO_NAME}"
DOCKER_TAG="${ECR_REPO_URL}:${DOCKER_TAG_VERSION}"
DOCKER_BUILD_CONTEXT="git@github.com:bu-ist/kuali-research-docker.git#${DOCKER_BUILD_CONTEXT_GIT_BRANCH}:apache-shib/build.context"

# 3) Build the image
docker build -t ${DOCKER_TAG} ${DOCKER_BUILD_CONTEXT}
