secureScript.script:
if(!GIT_PULL) {
  return "<input type='text' readonly=true name='value' style='width:400px; font-family:monospace;' disabled=true>"
}

def url = "git@github.com:bu-ist/kuali-core-main.git"
return "<input type='text' readonly=true disabled=true name='value' style='width:400px; font-family:monospace;' value='" +  url + "'>"

if(!GIT_PULL) {
   def list = ['N/A:selected']
}
else {
   def list = ['Branch:selected', 'Tag', 'Commit ID']
}

if(!GIT_PULL) {
  return "<input type='text' readonly=true name='value' style='width:400px; font-family:monospace;' disabled=true>"
}

// Instantiate a string builder to build a dropdown box populated with all the branches in the target git repo
def sb = new StringBuilder("<select name='value' style='width:400px; font-family:monospace;'>")
def defaultValue = null
def process = null
def cmd = "ssh-add /var/lib/jenkins/.ssh/bu_github_id_core_rsa > /dev/null 2>&1; "

// Create a command to that passed to ssh-agent to run with sh that will acquire the list of branches in the target git repo
if ("branch".equalsIgnoreCase(GIT_REF_TYPE)) {
   defaultValue = 'coi-ui-1802.0001'
   cmd = cmd +
      "git -c core.askpass=true ls-remote -h ${GIT_REPO_URL} | " + 
      "grep -i -P -o '^[^\\s]+\\s+refs/heads/\\K(.*)\$'"
} 
else if ("tag".equalsIgnoreCase(GIT_REF_TYPE)) {
   cmd = cmd +
      "git -c core.askpass=true ls-remote -t ${GIT_REPO_URL} | " + 
      "grep -i -P -o '^[^\\s]+\\s+refs/tags/\\K(.*)\$'"
}
else {
    return "<input type='text' readonly=true name='value' style='width:400px; font-family:monospace;' disabled=true>"
}

def cmdarr = [ "ssh-agent", "sh", "-c", cmd ]
process = new ProcessBuilder(cmdarr).start()

// Iterate over the input stream produced by having run the process (should be git ls-remote standard output)
process.inputStream.eachLine {
   if( "${it}".equals(defaultValue)) {
      sb.append("<option selected=true value='").append("${it}").append("'>").append("${it}").append("</option>")
   }
   else {
      sb.append("<option value='").append("${it}").append("'>").append("${it}").append("</option>")
   }
}
process.waitForOrKill(20000);
sb.append("</select>")
return sb.toString()

def commitId = null
def process = null
def cmd = "ssh-add /var/lib/jenkins/.ssh/bu_github_id_core_rsa > /dev/null 2>&1; "

if("commit id".equalsIgnoreCase(GIT_REF_TYPE)) {
    return "<input type='text' name='value' style='font-family:monospace; width:400px;'>"
}

if ("branch".equalsIgnoreCase(GIT_REF_TYPE)) {
   cmd = cmd +
      "git -c core.askpass=true ls-remote -h ${GIT_REPO_URL} refs/heads/ ${GIT_REF} | " + 
      "grep -i -P -o '^[^\\s]+'"
}
else if ("tag".equalsIgnoreCase(GIT_REF_TYPE)) {
   cmd = cmd +
      "git -c core.askpass=true ls-remote -t ${GIT_REPO_URL} refs/tags/ ${GIT_REF} | " + 
      "grep -i -P -o '^[^\\s]+'"
}

def cmdarr = [ "ssh-agent", "sh", "-c", cmd ]
process = new ProcessBuilder(cmdarr).start()

// Iterate over the input stream produced by having run the process (should have only one line).
process.inputStream.eachLine {
   if(commitId == null) {
      commitId = "${it}"
   }
}
process.waitForOrKill(20000);


if(commitId == null) {
   return "<input type='text' name='value' disabled=true style='font-family:monospace; width:400px;'>"
}
else {
   return "<input type='text' name='value' disabled=true value='" + commitId + "' style='font-family:monospace; width:400px;'>"
}

if("${GIT_REF}".matches("^\\d{4}\\.\\d{4}\$")) {
   return "<input type='text' name='value' value='${GIT_REF}' style='font-family:monospace; width:400px;'>"
}
else {
   return "<input type='text' name='value' style='font-family:monospace; width:400px;'>"
}
