secureScript.script:
if("available".equalsIgnoreCase(VERSION_OPT)) {
   def sb = new StringBuilder("<select name='value' style='width:400px;font-family:monospace;'>")
   def multiline = 'docker images -q --filter=reference=730096353738.dkr.ecr.us-east-1.amazonaws.com/core:* --format {{.Tag}}'.execute()
   multiline.waitForOrKill(20000)
   def lines = multiline.text.readLines()
   lines.each {
      sb.append("<option value='${it}'>${it}</option>")
   }
   sb.append("</select>")
   return sb.toString()
}
else {
   return "<input type='text' name='value' style='width:400px;font-family:monospace'>"
}
