secureScript.script:
// Instantiate a string builder to build a dropdown box populated with all the branches in the target git repo
def sb = new StringBuilder("<select name='value' style='width:400px; font-family:monospace;'>")
def defaultBranch = 'master'
// Create a command to that passed to ssh-agent to run with sh that will acquire the list of branches in the target git repo
def cmd = 
   "ssh-add /var/lib/jenkins/.ssh/bu_github_id_docker_rsa > /dev/null 2>&1; " + 
   "git -c core.askpass=true ls-remote -h ${GIT_REPO_URL} | " + 
   "grep -i -P -o '^[^\\s]+\\s+refs/heads/\\K(.*)\$'"

// Create an array for the ProcessBuilder and run the process.
// Cannot use execute command because of use of subprocess with sh.
def cmdarr = [ "ssh-agent", "sh", "-c", cmd ]
def process = new ProcessBuilder(cmdarr).start()

// Iterate over the input stream produced by having run the process (should be git ls-remote standard output)
process.inputStream.eachLine {
   if( "${it}" == defaultBranch) {
      sb.append("<option selected=true value='").append("${it}").append("'>").append("${it}").append("</option>")
   }
   else {
      sb.append("<option value='").append("${it}").append("'>").append("${it}").append("</option>")
   }
}
process.waitFor();
sb.append("</select>")
return sb.toString()

def sb = new StringBuilder("<select name='value' style='width:400px;font-family:monospace;'>")
