secureScript.script:
def sb = new StringBuilder("<div style='padding-left:20px; margin-bottom:30px;'>What landscape do you want to deploy to?<br><select name='value' style='width:200px;font-family:monospace; margin-top:8px;'>")
sb.append("<option selected=true value='qa'>qa</option>")
sb.append("<option value='stage'>stage</option>")
sb.append("<option value='prod'>prod</option>")
sb.append("</select></div>")
return sb.toString()

def GIT_REPO_URL="https://github.com/bu-ist/kuali-research"
def sb = new StringBuilder("<div style='padding-left:20px'>Details<br>")
sb.append("<pre style='width: 600px; background-color:#ebe0e0; padding:10px; border:1px solid black; margin:5px;'name='value'>")

// 1) GET THE TAG THE DOCKER IMAGE CAN BE IDENTIFIED WITH IN THE DOCKER REGISTRY
def version = "ERROR!!! Cannot determine version"
def process = ('cat /var/lib/jenkins/jobs/kuali-research-3-docker-push-image/lastSuccessful/log').execute()
process.waitForOrKill(20000)
def list = process.text.readLines()
list.each {
      if ("${it}".contains("digest")) {
            version = "${it}".split("\\s+")[0].replaceAll("[^\\.\\d]", "").trim()
      }
}
sb.append("DOCKER REGISTRY:\n(deploying from the following docker registry URI): ").append("\n\n    730096353738.dkr.ecr.us-east-1.amazonaws.com/coeus:").append(version).append("\n\n")

// 2) GET THE GIT REFERENCE FOR THE SOURCE CODE THAT THE DOCKER IMAGE IS BASE ON
def process2 = ("git -c core.askpass=true ls-remote -h " + GIT_REPO_URL + " refs/heads/bu-master").execute() | 'grep -i -P -o ^[^\\s]+'.execute()
def commitId = "ERROR!!! Cannot get git commit reference"
if (process2 != null) {
   process2.waitForOrKill(20000)
   list = process2.text.readLines()
   if (list != null && ! list.isEmpty()) {
      commitId = list.get(0)
   }
}
sb.append("GIT COMMIT\n(Assumes the above docker image was built on the current HEAD  \n of the bu-master branch):").append("\n\n").append("    ").append(commitId).append("\n")

sb.append("</pre>")
return sb.toString()

hudson.tasks.Shell.command:
set +x
CLI=/var/lib/jenkins/jenkins-cli.jar HOST=http://localhost:8080/ DOCKER_PUSH_LOG_FILE=/var/lib/jenkins/jobs/kuali-research-3-docker-push-image/lastSuccessful/log EC2_IDS=("i-0534c4e38e6a24009" "i-07d7b5f3e629e89ae");
source /var/lib/jenkins/cli-credentials.sh
POM_VERSION=$(cat ${DOCKER_PUSH_LOG_FILE} | grep -P 'digest' | cut -d ':' -f 1 | tr -d '[[:space:]]');
case "$LANDSCAPE" in "qa") EC2_IDS=("i-011ccd29dec6c6d10");; "stage") EC2_IDS=("i-090d188ea237c8bcf" "i-0cb479180574b4ba2");; "prod") EC2_IDS=("i-0534c4e38e6a24009" "i-07d7b5f3e629e89ae");; esac
for i in $(seq ${#EC2_IDS[@]}); do EC2_ID=${EC2_IDS[i-1]}
java -jar ${CLI} \ -s ${HOST} build 'kuali-research-4-docker-run-container' -v -f \ -p EC2_INSTANCE_ID=${EC2_ID} \ -p POM_VERSION=${POM_VERSION}
eval $CMD
done
set -x

hudson.plugins.emailext.ExtendedEmailPub.presendScript:
