secureScript.script:
def list = null
if ("branch".equalsIgnoreCase(GIT_REF_TYPE)) {
   def multiline = ('git -c core.askpass=true ls-remote -h ' + GIT_REPO_URL).execute() | 'grep -i -P -o ^[^\\s]+\\s+refs/heads/\\K(.*)\$'.execute()
   multiline.waitForOrKill(20000)
   list = multiline.text.readLines()
} 
else if ("tag".equalsIgnoreCase(GIT_REF_TYPE)) {
   def multiline = ('git -c core.askpass=true ls-remote -t ' + GIT_REPO_URL).execute() | 'grep -i -P -o ^[^\\s]+\\s+refs/tags/\\K(.*)\$'.execute() | 'grep -i -P ^[^\\^\\{\\}]+$'.execute()
   multiline.waitForOrKill(20000)
   list = multiline.text.readLines()
}

if (list== null) {
   if ("commit id".equalsIgnoreCase(GIT_REF_TYPE)) {
      return "<input type='text' name='value' style='width:310px;' disabled=true>"
   }
   else {
      return "<input type='text' name='value' style='width:310px;' disabled=true>"
    }
}
else {
   def sb = new StringBuilder("<select name='value' style='width:310px;'>")
   list.each {
      sb.append("<option value='").append("${it}").append("'>").append("${it}").append("</option>")
   }
   sb.append("</select>")
   return sb.toString()
}

def process = null
def commitId = null
if ("branch".equalsIgnoreCase(GIT_REF_TYPE)) {
   process = ("git -c core.askpass=true ls-remote -h " + GIT_REPO_URL + " refs/heads/' " + GIT_REF).execute() | 'grep -i -P -o ^[^\\s]+'.execute()
}
else if ("tag".equalsIgnoreCase(GIT_REF_TYPE)) {
   process = ("git -c core.askpass=true ls-remote -t " + GIT_REPO_URL + " refs/tags/' " + GIT_REF + "^{}").execute() | 'grep -i -P -o ^[^\\s]+'.execute()
}
else if("commit id".equalsIgnoreCase(GIT_REF_TYPE)) {
    process = "echo N/A".execute()
}
else {
   def branch = null
   if("ci".equalsIgnoreCase(LANDSCAPE)) {
      branch = "bu-master"
   }
   if("sandbox".equalsIgnoreCase(LANDSCAPE)) {
      branch = "master"
   }
   if(branch != null) {
      //process = "echo ${branch}".execute()
      process = ("git -c core.askpass=true ls-remote -h " + GIT_REPO_URL + " refs/heads/" + branch).execute() | 'grep -i -P -o ^[^\\s]+'.execute()
   }
}

if (process != null) {
   process.waitForOrKill(20000)
   def list = process.text.readLines()
   if (list == null || list.isEmpty()) {
      commitId = "NO RESULTS!!!"
   }
   else {
      commitId = list.get(0)
   }
}


if(commitId == null) {
   return "<input type='text' name='value' disabled=true style='font-family:monospace; width:310px;'>"
}
else if ( ! "commit id".equalsIgnoreCase(GIT_REF_TYPE)) {
   return "<input type='text' name='value' value='" + commitId + "' readonly=true style='font-family:monospace; width:310px;'>"
}
else {
   return "<input type='text' name='value' style='font-family:monospace; width:310px;'>"
}

